mapper.select:
SELECT COUNT(*)
FROM ChatRoom CR
         LEFT JOIN Users U ON CR.usersIdx = U.usersIdx


        AND CONCAT(U.usersAccount) REGEXP #{search}


        AND CONCAT(CR.roomName) REGEXP #{search}

SELECT CR.idx, CR.roomName, CR.companysIdx, CR.createdDate, U.usersAccount, U.usersRole FROM ChatRoom CR LEFT JOIN Users U ON CR.usersIdx = U.usersIdx
AND CONCAT(U.usersAccount) REGEXP #{search}
AND CONCAT(CR.roomName) REGEXP #{search}
ORDER BY CR.createdDate DESC LIMIT #{startIndex}, #{pageSize}
