mapper.select:
SELECT COUNT(*)
FROM Companys C
WHERE C.companysEnabled = #{companysEnabled}

    AND CONCAT(C.companysName) REGEXP #{search}


    AND CONCAT(C.companysCeoName) REGEXP #{search}


    AND CONCAT(C.companysPhone) REGEXP #{search}

SELECT C.companysIdx, C.companysName, C.companysCeoName, C.companysPhone, C.companysRegion1, C.companysRegion2, C.companysRegion3, C.companysRegistDate, C.companysPremium, C.companysLocalPremium, C.companysEnabled, C.companysDelete, T.companysCategoryValue FROM Companys C JOIN (SELECT CT.companysIdx, GROUP_CONCAT(CT.companysCategoryValue SEPARATOR '|') companysCategoryValue FROM CompanysCategory CT GROUP BY CT.companysIdx) T ON C.companysIdx = T.companysIdx WHERE C.companysEnabled = #{companysEnabled}
AND CONCAT(C.companysName) REGEXP #{search}
AND CONCAT(C.companysCeoName) REGEXP #{search}
AND CONCAT(C.companysPhone) REGEXP #{search}
ORDER BY C.companysRegistDate DESC LIMIT #{startIndex}, #{pageSize}
