mapper.insert:
INSERT INTO Region(regionIdx, regionName, regionPrice, regionSelectable)
VALUES(#{regionIdx}, #{regionName}, #{regionPrice}, #{regionSelectable})
ON DUPLICATE KEY UPDATE regionPrice = #{regionPrice}

mapper.update:
UPDATE Region
SET regionSelectable = 0
WHERE regionIdx = #{regionIdx}

UPDATE Region
SET regionSelectable = 1
WHERE regionIdx = #{regionIdx}
